import axios from 'axios'

const ShowWeather = (props) => {
    const [weather, setWeather] = useState([])

    const url = 'https://api.openweathermap.org/data/3.0/onecall?lat='+props.lat+'&lon='+props.lon+'&units=imperial&exclude=hourly,daily,minutely,alerts&appid=9e2f664a07374c9d4801674576bf7567'
    console.log("url = ", url)

    useEffect(() => {
      axios
        .get(url)
        .then(response => {
          setWeather(response.data)
        })
    }, [])

    console.log("weather = ", weather)

