const Header = (props) => {
  return (
    <h1>
      {props.course}
    </h1>
  )
}

const Content = (props) => {
  return (
    <p>
      {props.name} {props.exercises}
    </p>
  )
}

const Total = (array) => {
  let sum = 0
  for (let i = 0; i< array.length; i += 1) {
    sum+= array [i]
  }
  console.log(sum)
  return sum
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  const TotalExercises = [20,10,10]

  return (
    <div>
      <Header course={course} />
      <Content name={parts[0].name} exercises={parts[0].exercises} />
      <Content name={parts[1].name} exercises={parts[1].exercises} />
      <Content name={parts[2].name} exercises={parts[2].exercises} />
      <Total array={TotalExercises} />
    </div>
  )
}

export default App
