import { useState } from 'react'
import "./App.css";

const StatisticLine = (props) => {
  return (
    <table>
      <tr>
        <td>{props.text}</td>
        <td>{props.variable}</td>
      </tr>
    </table>
    //  <p>{props.text} {props.variable}</p>
  )
}

const Statistics = (props) => {
  return (
    <div>
      <h1>statistics</h1>
      <StatisticLine text="good" />
      <StatisticLine variable={props.good} />
      <StatisticLine text="neutral" />
      <StatisticLine variable={props.neutral} />
      <StatisticLine text="bad" />
      <StatisticLine variable={props.bad} />
      <StatisticLine text="all" variable={props.all} />
      <StatisticLine text="average" variable={props.average.toFixed(2)} />
      <StatisticLine text="positive" variable={(props.positive * 100).toFixed(0) + "%"} />
    </div>
  )
}

const Button = (props) => {
  return (
    <button onClick={props.action}>{props.text}</button>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)
  const [average, setAverage] = useState(0)
  const [positive, setPositive] = useState(0)

  const handleGood = () => {
    setGood(good + 1)
    setAll(all + 1);
    setAverage((good - bad + 1) / (all + 1))
    setPositive((good + 1) / (all + 1))
  }

  const handleNeutral = () => {
    setNeutral(neutral + 1)
    setAll(all + 1)
    setAverage((good - bad) / (all + 1))
    setPositive(good / (all + 1))
  }

  const handleBad = () => {
    setBad(bad + 1)
    setAll(all + 1)
    setAverage((good - bad - 1) / (all + 1))
    setPositive(good / (all + 1))
  }

  if (all > 0) {
    return (
      <div>
        <h1>give feedback</h1>
        <Button action={handleGood} text="good" />
        <Button action={handleNeutral} text="neutral" />
        <Button action={handleBad} text="bad" />
        <Statistics good={good} bad={bad} neutral={neutral} all={all} average={average} positive={positive} />
      </div>
    )
  } else {
    return (
      <div>
        <h1>give feedback</h1>
        <button onClick={handleGood}>good</button>
        <button onClick={handleNeutral}>neutral</button>
        <button onClick={handleBad}>bad</button>
        <h1>statistics</h1>
        <p>No feedback given</p>
      </div>
    )
  }
}

export default App
