import { useState } from 'react'

const Statistics = (props) => {
  const [average, setAverage] = useState(0)
  const [positive, setPositive] = useState(0)

  // calculate average
  if (all > 0) {
    setAverage((props.good * 1 + props.bad * -1) / props.all)
  }
  console.log(props.good)

  //calculate % positive
  if (props.all > 0) {
    setPositive(props.good / (props.all))
  }

  return (
    <p> positive {positive} <br></br>
      average {average} </p>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)

  const handleGood = () => {
    setGood(good + 1)
    setAll(all + 1)
    Statistics
  }

  const handleNeutral = () => {
    setNeutral(neutral + 1)
    setAll(all + 1)
  }

  const handleBad = () => {
    setBad(bad + 1)
    setAll(all + 1)
  }

  /* const HandleAverage = () => {
    if (all > 0) {
      setAverage((good * 1 + bad * -1) / all)
    }
    return (
      <div>
        average {average}
      </div>
    )
  } */

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={handleGood}>good</button>
      <button onClick={handleNeutral}>neutral</button>
      <button onClick={handleBad}>bad</button>
      <h1>statistics</h1>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
      <p>all {all}</p>
      {/* <HandleAverage /> */}
      <Statistics good={good} bad={bad} all={all} />
    </div>
  )
}

export default App
