import "./App.css"

//shit we need for sum
const initialValue = 0

const Sum = (props) => {
  const array = props.array
  console.log("array = ", array)
  {
    array.reduce((sum, amount) => {
      return (
        <div id="bold">total of {sum + amount.exercises} exercises</div>
      )
    }, initialValue)
  }
}

const Course = (props) => {
  //make course a local array
  const courselist = props.courses.map((x) => x)
  console.log("courselist = ", courselist)

  //seperate parts from course (just did this to learn)
  var parts = courselist[0].parts.map((x) => x)
  console.log("parts = ", parts)



  return (
    <div>
      {/* get all courses */}
      {courselist.map((part, i) =>
        <div key={part.id}>
          <h1>{part.name}</h1>

          {/* get all parts */}
          {courselist[i].parts.map((subpart, i) =>
            <p key={subpart.id}>
              {subpart.name} {subpart.exercises}
            </p>
          )}

          {/* sum parts in this course*/}
          {courselist[i].parts.reduce((sum, amount) => {
            return (
              <div id="bold">total of {sum+amount.exercises} exercises</div>
            )
          }, initialValue)
          }
          <Sum array={courselist[i].parts} />

        </div>
      )}
    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <div>
      <Course courses={courses} />
    </div>
  )
}

export default App
