import "./App.css"

const Course = (props) => {
  //make course a local array
  const courselist = { ... props.courses }
  console.log(courselist);

  // get course names
  const coursenames = courselist.map((part, i) =>
  <p key={part.id}>
{part.name}
  </p>)
  console.log("course names = ", coursenames)

  //seperate parts from course
  var parts = courselist.parts

  return (
    <div>
      <h1>{props.course.name}</h1>
      {parts.map((part, i) =>
        <p key={part.id}>
          {part.name} {part.exercises}
        </p>
      )}
    </div>
  )
}

