import React from 'react'
import peopleService from './services/persons'
import { useState, useEffect } from 'react'

const ShowList = (props) => {
    const [persons, setPersons] = useState([])

    useEffect(() => {
        peopleService
          .getAll()
          .then(initialNames => {
            setPersons(initialNames)
          })
      }, [])

    const toggleTrash = id => {

        peopleService
            .erase(id)
            .then(person => {
                setPersons(persons.filter(person => person.id !== id ))
            })

        console.log("id ", id)
    }

    return (
        <div>
            <h2>Numbers</h2>
            {/* iterates through each part of array */}
            {props.persons.map((subpart, i) => {
                // checks if search is included in name
                if (subpart.name.includes(props.search)) {
                    return (
                        <p key={subpart.name}>
                            {subpart.name} {subpart.number}{" "}
                            {console.log("id ", subpart.id)}
                            <button onClick={toggleTrash(props.id)}>delete?</button>
                        </p>)
                }
            }
            )}
        </div>
    )
}

export default ShowList
