import { useState } from 'react'
import "./App.css"

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')

  // what happens on submit (name gets added to array)
  const addName = (event) => {
    event.preventDefault()
    const nameObject = {
      name: newName
    }

    setPersons(persons.concat(nameObject))
    setNewName('')
  }

  // event handler that tracks what's written in box
  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addName}>
        <div>
          name: <input
            value={newName}
            /* put conditional here */
            onChange={persons.includes(newName) ? window.alert() : handleNameChange}
          />
        </div>
        <div>newName: {newName}</div>
        <div>match?: {persons.includes(newName)}</div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map((subpart,i) => 
      <p key={subpart.name}>
        {subpart.name}
      </p>
      )}
    </div>
  )
}

export default App
