import React from 'react'
import peopleService from './services/persons'
import { useState, useEffect } from 'react'

const ShowList = (props) => {

    const toggleTrash = (event) => {
        event.preventDefault()   

        peopleService
                .erase(props.id)
                .then(returnedNote => {
                    console.log("returnedNote ", returnedNote)
                    props.setPersons(props.persons.filter(word => word.id !== returnedNote))
                })
      }

    return (
        <div>
            <h2>Numbers</h2>
            {/* iterates through each part of array */}
            {props.persons.map((subpart, i) => {
                // checks if search is included in name
                if (subpart.name.includes(props.search)) {
                    return (
                        <p key={subpart.name}>
                            {subpart.name} {subpart.number}{" "}
                           <button onClick={() => {
                            toggleTrash(subpart.id)
                           }}
                           >
                            delete</button>
                        </p>)
                }
            }
            )}
        </div>
    )
}

export default ShowList
