import { useState } from 'react'
import "./App.css"

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [search, setSearch] = useState('')


  // what happens on submit (name and number get added to array)
  const addName = (event) => {
    event.preventDefault()
    const nameObject = {
      name: newName,
      number: newNumber
    }

    //check if name is already in list
    if (persons.some(object => object.name === newName)) {
      window.alert(`${newName} is already added to phonebook`)
    } else {
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')
    }
  }

  // event handler that tracks what's searched
  const handleSearchChange = (event) => {
    setSearch(event.target.value)
  }

  // event handler that tracks what's written in name box
  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  // event handler that tracks what's written in number box
  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter shown with <input
          value={search}
          onChange={handleSearchChange}
        />
      </div>
      <h2>add a new</h2>
      <form onSubmit={addName}>
        <div>
          name: <input
            value={newName}
            onChange={handleNameChange}
          />
        </div>
        <div>
          number: <input
            value={newNumber}
            onChange={handleNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>

      <h2>Numbers</h2>
      {/* iterates through each part of array */}
      {persons.map((subpart, i) => {
       /* checks if search is included in name */
       if (subpart.name.includes(search)) {
          return (
            <p key={subpart.name}>
              {subpart.name} {subpart.number}
            </p>)
        } else if (search === "") {
          return (
            <p key={subpart.name}>
              {subpart.name} {subpart.number}
            </p>)
        } else {

        }
      }
      )}
    </div>
  )
}

export default App
