import { useState } from 'react'
import "./App.css"

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')

  // what happens on submit (name gets added to array)
  const addName = (event) => {
    event.preventDefault()
    const nameObject = {
      name: newName
    }
    if (persons.some(object => object.name === newName)) {
      window.alert()
    } else {
      setPersons(persons.concat(nameObject))
      setNewName('')
    }
  }

  // event handler that tracks what's written in box
  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  console.log(persons)
  console.log(
    persons.some(object => object.name === newName)
  )

  // persons.some(object => object.name === newName) ? window.alert() :

  return (
    <div>
      <h2>Phonebook</h2>
      {/* check if name is already in list, if so reject it */}
      <form onSubmit={addName}>
        <div>
          name: <input
            value={newName}
            onChange={handleNameChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>

      <h2>Numbers</h2>
      {persons.map((subpart, i) =>
        <p key={subpart.name}>
          {subpart.name}
        </p>
      )}
    </div>
  )
}

export default App
